import{_ as a,o,c as i,a2 as t}from"./chunks/framework.CUre4L63.js";const p=JSON.parse('{"title":"Theme-Aware Logo Setup","description":"","frontmatter":{},"headers":[],"relativePath":"public/README.md","filePath":"public/README.md","lastUpdated":1765184252000}'),s={name:"public/README.md"};function r(n,e,l,h,c,d){return o(),i("div",null,[...e[0]||(e[0]=[t('<h1 id="theme-aware-logo-setup" tabindex="-1">Theme-Aware Logo Setup <a class="header-anchor" href="#theme-aware-logo-setup" aria-label="Permalink to &quot;Theme-Aware Logo Setup&quot;">​</a></h1><p>The Vitals documentation now supports theme-aware logos that change based on light/dark mode.</p><h2 id="current-setup" tabindex="-1">Current Setup <a class="header-anchor" href="#current-setup" aria-label="Permalink to &quot;Current Setup&quot;">​</a></h2><ul><li><strong>Light Mode</strong>: <code>Docs/public/icon-light.png</code></li><li><strong>Dark Mode</strong>: <code>Docs/public/icon-dark.png</code></li></ul><h2 id="customizing-the-logos" tabindex="-1">Customizing the Logos <a class="header-anchor" href="#customizing-the-logos" aria-label="Permalink to &quot;Customizing the Logos&quot;">​</a></h2><p>Currently, both <code>icon-light.png</code> and <code>icon-dark.png</code> are copies of the original <code>icon.png</code>. To create proper theme-specific versions:</p><h3 id="for-light-mode-icon-light-png" tabindex="-1">For Light Mode (<code>icon-light.png</code>) <a class="header-anchor" href="#for-light-mode-icon-light-png" aria-label="Permalink to &quot;For Light Mode (`icon-light.png`)&quot;">​</a></h3><ul><li>Use darker colors that work well on white/light backgrounds</li><li>Ensure good contrast against light gray backgrounds</li><li>Consider using black or dark blue tones</li></ul><h3 id="for-dark-mode-icon-dark-png" tabindex="-1">For Dark Mode (<code>icon-dark.png</code>) <a class="header-anchor" href="#for-dark-mode-icon-dark-png" aria-label="Permalink to &quot;For Dark Mode (`icon-dark.png`)&quot;">​</a></h3><ul><li>Use lighter colors that work well on dark backgrounds</li><li>Ensure good contrast against dark gray backgrounds</li><li>Consider using white or bright colors</li><li>You can add glow effects or neon-style colors for modern dark theme aesthetics</li></ul><h2 id="how-to-update" tabindex="-1">How to Update <a class="header-anchor" href="#how-to-update" aria-label="Permalink to &quot;How to Update&quot;">​</a></h2><ol><li>Design your light and dark versions</li><li>Export as PNG files (recommended size: 400x400 to 600x600 pixels)</li><li>Replace: <ul><li><code>Docs/public/icon-light.png</code> with your light theme version</li><li><code>Docs/public/icon-dark.png</code> with your dark theme version</li></ul></li></ol><h2 id="tools-for-creating-theme-variants" tabindex="-1">Tools for Creating Theme Variants <a class="header-anchor" href="#tools-for-creating-theme-variants" aria-label="Permalink to &quot;Tools for Creating Theme Variants&quot;">​</a></h2><ul><li><p><strong>Photoshop/GIMP</strong>: Adjust brightness, contrast, and apply color filters</p></li><li><p><strong>Figma</strong>: Create separate variants with different color schemes</p></li><li><p><strong>ImageMagick</strong> (CLI): Batch process to invert colors:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Invert for dark theme</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">convert</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> icon.png</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -negate</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> icon-dark.png</span></span>\n<span class="line"></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Adjust brightness/contrast</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">convert</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> icon.png</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -brightness-contrast</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> 20x20</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> icon-dark.png</span></span></code></pre></div></li></ul><h2 id="where-logos-appear" tabindex="-1">Where Logos Appear <a class="header-anchor" href="#where-logos-appear" aria-label="Permalink to &quot;Where Logos Appear&quot;">​</a></h2><ul><li><strong>Navbar</strong>: Small logo next to site title (changes with theme)</li><li><strong>Homepage Hero</strong>: Large logo in the center of landing page (changes with theme)</li><li><strong>Favicon</strong>: Browser tab icon (currently uses default icon.png)</li></ul><p>The logos automatically switch when users toggle between light and dark themes.</p>',17)])])}const u=a(s,[["render",r]]);export{p as __pageData,u as default};
